-module(test_client).
-author("dmitryditas").

-behaviour(gen_server).

-include_lib("kv_pb.hrl").
-include_lib("common.hrl").

%% API
-export([
    start_link/0,
    set/0,
    get/0
]).

%% gen_server callbacks
-export([init/1,
    handle_call/3,
    handle_cast/2,
    handle_info/2,
    terminate/2,
    code_change/3]).

-define(SERVER, ?MODULE).

-define(SET_REQ, #set_request{req = #data{key = "key16", value = ""}}).
-define(SET_RESP, #set_response{error = ok}).
-define(GET_REQ, #get_request{key = "key16"}).
-define(GET_RESP, #get_response{error = ok, req = #data{key = <<"key16">>, value = <<"">>}}).

-record(state, {
    socket,
    bin = <<>>
}).

%%%===================================================================
%%% API
%%%===================================================================
set() ->
    gen_server:call(?SERVER, set).

get() ->
    gen_server:call(?SERVER, get).

%%--------------------------------------------------------------------
%% @doc
%% Starts the server
%%
%% @end
%%--------------------------------------------------------------------
-spec(start_link() ->
    {ok, Pid :: pid()} | ignore | {error, Reason :: term()}).
start_link() ->
    gen_server:start_link({local, ?SERVER}, ?MODULE, [], []).

%%%===================================================================
%%% gen_server callbacks
%%%===================================================================

%%--------------------------------------------------------------------
%% @private
%% @doc
%% Initializes the server
%%
%% @spec init(Args) -> {ok, State} |
%%                     {ok, State, Timeout} |
%%                     ignore |
%%                     {stop, Reason}
%% @end
%%--------------------------------------------------------------------
-spec(init(Args :: term()) ->
    {ok, State :: #state{}} | {ok, State :: #state{}, timeout() | hibernate} |
    {stop, Reason :: term()} | ignore).
init([]) ->
    {ok, S} = gen_tcp:connect("127.0.0.1", 5555, [binary, {active, true}]),
    {ok, #state{socket = S}}.

%%--------------------------------------------------------------------
%% @private
%% @doc
%% Handling call messages
%%
%% @end
%%--------------------------------------------------------------------
-spec(handle_call(Request :: term(), From :: {pid(), Tag :: term()},
    State :: #state{}) ->
    {reply, Reply :: term(), NewState :: #state{}} |
    {reply, Reply :: term(), NewState :: #state{}, timeout() | hibernate} |
    {noreply, NewState :: #state{}} |
    {noreply, NewState :: #state{}, timeout() | hibernate} |
    {stop, Reason :: term(), Reply :: term(), NewState :: #state{}} |
    {stop, Reason :: term(), NewState :: #state{}}).
handle_call(set, _From, #state{socket = S} = State) ->
    BinMsg = kv_pb:encode_msg(#req_envelope{type = 1, set_req = ?SET_REQ}),
    Res = gen_tcp:send(S, BinMsg),
    {reply, Res, State};
handle_call(get, _From, #state{socket = S} = State) ->
    BinMsg = kv_pb:encode_msg(#req_envelope{type = 3, get_req = ?GET_REQ}),
    Res = gen_tcp:send(S, BinMsg),
    {reply, Res, State};
handle_call(_Request, _From, State) ->
    {reply, ok, State}.

%%--------------------------------------------------------------------
%% @private
%% @doc
%% Handling cast messages
%%
%% @end
%%--------------------------------------------------------------------
-spec(handle_cast(Request :: term(), State :: #state{}) ->
    {noreply, NewState :: #state{}} |
    {noreply, NewState :: #state{}, timeout() | hibernate} |
    {stop, Reason :: term(), NewState :: #state{}}).
handle_cast(_Request, State) ->
    {noreply, State}.

%%--------------------------------------------------------------------
%% @private
%% @doc
%% Handling all non call/cast messages
%%
%% @spec handle_info(Info, State) -> {noreply, State} |
%%                                   {noreply, State, Timeout} |
%%                                   {stop, Reason, State}
%% @end
%%--------------------------------------------------------------------
-spec(handle_info(Info :: timeout() | term(), State :: #state{}) ->
    {noreply, NewState :: #state{}} |
    {noreply, NewState :: #state{}, timeout() | hibernate} |
    {stop, Reason :: term(), NewState :: #state{}}).
handle_info({tcp, _, BinMsg}, #state{bin = Bin} = State) ->
    State1 = case catch kv_pb:decode_msg(<<Bin/binary, BinMsg/binary>>, req_envelope) of
             #req_envelope{type = set_response_t, set_resp = Resp} ->
                 
                 ?SET_RESP = Resp,
                 
                 State#state{bin = <<>>};
             #req_envelope{type = get_response_t, get_resp = Resp} ->
                 
                 ?GET_RESP = Resp,
    
                 State#state{bin = <<>>};
             {'EXIT', _Reason} ->
                 State#state{bin = <<Bin/binary, BinMsg/binary>>}
         end,
    {noreply, State1};
handle_info(_Info, State) ->
    {noreply, State}.

%%--------------------------------------------------------------------
%% @private
%% @doc
%% This function is called by a gen_server when it is about to
%% terminate. It should be the opposite of Module:init/1 and do any
%% necessary cleaning up. When it returns, the gen_server terminates
%% with Reason. The return value is ignored.
%%
%% @spec terminate(Reason, State) -> void()
%% @end
%%--------------------------------------------------------------------
-spec(terminate(Reason :: (normal | shutdown | {shutdown, term()} | term()),
    State :: #state{}) -> term()).
terminate(_Reason, _State) ->
    ok.

%%--------------------------------------------------------------------
%% @private
%% @doc
%% Convert process state when code is changed
%%
%% @spec code_change(OldVsn, State, Extra) -> {ok, NewState}
%% @end
%%--------------------------------------------------------------------
-spec(code_change(OldVsn :: term() | {down, term()}, State :: #state{},
    Extra :: term()) ->
    {ok, NewState :: #state{}} | {error, Reason :: term()}).
code_change(_OldVsn, State, _Extra) ->
    {ok, State}.

